package browse;

public class shuzhi {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int i=7;
	     int j=2;
	     System.out.println("i+j="+(i+j));
	     System.out.println("i-j="+(i-j));
	     System.out.println("i*j="+(i*j));
	     System.out.println("i/j="+(i/j));
	     Float a=7.0f;
	     int b=3;
	     System.out.println("a+b="+(a+b));
	     System.out.println("a-b="+(a-b));
	     System.out.println("a*b="+(a*b));
	     System.out.println("a/b="+(a/b));
	}

}
